------------------------------------------------------------
-- 1. Арифметика (collection_id = 1)
------------------------------------------------------------

-- Связывание арифметических знаний с коллекцией "Арифметика" (collection_id = 1)
SELECT k.id, 1
FROM knowledges k
WHERE k.id BETWEEN 1 AND 36
ON CONFLICT DO NOTHING;

------------------------------------------------------------
-- 2. Алгебра (collection_id = 2)
------------------------------------------------------------

-- Связывание алгебраических знаний с коллекцией "Алгебра" (collection_id = 2)
-- Предполагаемые id для алгебры: 37 .. 84
SELECT k.id, 2
FROM knowledges k
WHERE k.id BETWEEN 37 AND 84
ON CONFLICT DO NOTHING;

------------------------------------------------------------
-- 4. Тригонометрия (collection_id = 4)
------------------------------------------------------------

-- Связывание тригонометрических знаний с коллекцией "Тригонометрия" (collection_id = 4)
-- Предполагаемые id для тригонометрии: 85 .. 124
SELECT k.id, 4
FROM knowledges k
WHERE k.id BETWEEN 85 AND 124
ON CONFLICT DO NOTHING;
